mapper.update:
update dish
category_id = #{categoryId}, name = #{name}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select dish.*,category.name as category_name  from dish
    left join category on dish.category_id=category.id

    dish.category_id = #{categoryId}
    and dish.name like concat('%', #{name}, '%')
    and dish.status = #{status}

    order by id desc

select * from dish

    category_id = #{categoryId}
    and name like concat('%', #{name}, '%')
    and status = #{status}
