mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from setmeal

   category_id = #{categoryId}
   and name like concat('%', #{name}, '%')
   and status = #{status}

 order by create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
