mapper.select:
select * from shopping_cart

    user_id = #{userId}
    and setmeal_id = #{setmealId}
    and dish_id = #{dishId}
    and dish_flavor = #{dishFlavor}

mapper.update:
update shopping_cart
number = #{number}, create_time = #{createTime}, user_id = #{userId}, setmeal_id = #{setmealId}, dish_id = #{dishId}, dish_flavor = #{dishFlavor}, amount = #{amount},
where id = #{id}

mapper.delete:
delete from shopping_cart

    user_id = #{userId}
    and setmeal_id = #{setmealId}
    and dish_id = #{dishId}
    and dish_flavor = #{dishFlavor}
